#include <stdint.h>

int main(void)
{
	//addresses
    uint32_t *pClkControl = (uint32_t*) 0x40023830;
    uint32_t *pGpioDMode = (uint32_t*) 0x40020C00;
    uint32_t *pGpioDOutputData = (uint32_t*) 0x40020C14;

    //enables AHB1ENR clock
    *pClkControl |= (1<<3); //mask = 0x8

    //clear GPIO D12 mode then set to general purpose output mode
    *pGpioDMode &= ~(3<<24); // mask = 0xFCFFFFFF
    *pGpioDMode |= (1<<24);  // mask = 0x1000000

    //toggle led
    while(1)
    {
    //turns on gpio d 12 led
    *pGpioDOutputData |= (1<<12); //mask = 0x1000
    //delay
    for(uint32_t i=0; i < 100000; i++);
    //turn off led
    *pGpioDOutputData &= ~(1<<12);
    //delay
    for(uint32_t i=0; i < 100000; i++);
    }
}
