#include <stdint.h>
#include "main.h"


int main(void)
{

	RCC_AHB1ENR_t volatile *const pClkControl = (RCC_AHB1ENR_t*) 0x40023830;
	//Enable clock for GPIO D
	pClkControl->gpiod_en = 1;

	GPIOx_MODE_t volatile *const pGpioDMode = (GPIOx_MODE_t*) 0x40020C00;
	//Set GPIO D pin 12 to general purpose output mode
	pGpioDMode->pin_12 = 1;


    GPIOx_ODR_t volatile *const pGpioDOutputData = (GPIOx_ODR_t*) 0x40020C14;


    //toggle led
    while(1)
    {
    //turns on gpio d 12 led
    pGpioDOutputData->pin_12=1;

    //delay
    for(uint32_t i=0; i < 100000; i++);

    //turn off led
    pGpioDOutputData->pin_12=0;

    //delay
    for(uint32_t i=0; i < 100000; i++);
    }
}
